/** * $Id: SelfDefinedShiroRealm.java,v 1.0 18/5/4 下午10:44 chenmin Exp $ * <p> */package com.example.config.shiro;import com.example.bean.User;import lombok.extern.slf4j.Slf4j;import org.apache.shiro.authc.AuthenticationException;import org.apache.shiro.authc.AuthenticationInfo;import org.apache.shiro.authc.AuthenticationToken;import org.apache.shiro.authc.SimpleAuthenticationInfo;import org.apache.shiro.authz.AuthorizationInfo;import org.apache.shiro.authz.SimpleAuthorizationInfo;import org.apache.shiro.realm.AuthorizingRealm;import org.apache.shiro.subject.PrincipalCollection;/** * 自定义权限匹配和账号密码匹配 * @author chenmin * @version $Id: SelfDefinedShiroRealm.java,v 1.1 18/5/4 下午10:44 chenmin Exp $ * Created on 18/5/4 下午10:44 */@Slf4jpublic class SelfDefinedShiroRealm extends AuthorizingRealm {    /**     * 授权     * @param principals     * @return     */    @Override    protected AuthorizationInfo doGetAuthorizationInfo(PrincipalCollection principals) {        SimpleAuthorizationInfo authorizationInfo = new SimpleAuthorizationInfo();        return authorizationInfo;    }    /**     * 认证     * @param token     * @return     * @throws AuthenticationException     */    @Override    protected AuthenticationInfo doGetAuthenticationInfo(AuthenticationToken token)            throws AuthenticationException {        String username = (String) token.getPrincipal();        log.info(username);        SimpleAuthenticationInfo authorizationInfo = new SimpleAuthenticationInfo(                new User(username, "123"),                username,                getName()        );        return authorizationInfo;    }}