/** * $Id: SwaggerConfiguration.java,v 1.0 18/8/3 下午11:41 chenmin Exp $ * <p> */package com.example.config.swagger;import com.spring4all.swagger.EnableSwagger2Doc;import org.springframework.context.annotation.Configuration;import org.springframework.web.servlet.config.annotation.ResourceHandlerRegistry;import org.springframework.web.servlet.config.annotation.WebMvcConfigurerAdapter;/** * @author chenmin * @version $Id: SwaggerConfiguration.java,v 1.1 18/8/3 下午11:41 chenmin Exp $ * Created on 18/8/3 下午11:41 */@Configuration@EnableSwagger2Docpublic class SwaggerConfiguration extends WebMvcConfigurerAdapter {    @Override    public void addResourceHandlers(ResourceHandlerRegistry registry) {        registry.addResourceHandler("/js/**").addResourceLocations("classpath:/js/");        registry.addResourceHandler("swagger-ui.html")                .addResourceLocations("classpath:/META-INF/resources/");        registry.addResourceHandler("/webjars/**")                .addResourceLocations("classpath:/META-INF/resources/webjars/");    }}