/** * $Id: WebUtils.java,v 1.0 2019-01-22 00:49 chenmin Exp $ * <p> */package com.github.shiro.util;import javax.servlet.ServletException;import javax.servlet.ServletRequest;import javax.servlet.ServletResponse;import java.io.IOException;/** * @author chenmin * @version $Id: WebUtils.java,v 1.1 2019-01-22 00:49 chenmin Exp $ * Created on 2019-01-22 00:49 */public class WebUtils extends org.apache.shiro.web.util.WebUtils {    /**     * 请求转发     * @param request     * @param response     * @param url     * @throws ServletException     * @throws IOException     */    public static void sendForward(ServletRequest request, ServletResponse response, String url) throws ServletException, IOException {        request.getRequestDispatcher(url).forward(request, response);    }}