package info.chenmin.shiro.annotation;import info.chenmin.shiro.config.ShiroAutoConfiguration;import info.chenmin.shiro.config.ShiroRedisAutoConfiguration;import info.chenmin.shiro.swagger.SwaggerConfiguration;import org.springframework.context.annotation.Import;import java.lang.annotation.*;/** * 开启Shiro自动配置 */@Documented@Target(ElementType.TYPE)@Retention(RetentionPolicy.RUNTIME)@Import({ShiroAutoConfiguration.class, ShiroRedisAutoConfiguration.class, SwaggerConfiguration.class})public @interface EnableShiro {}